mapper.insert:
insert into goods_type (id, cat_name, cat_desc, 
  keywords, parent_id, sort_order
  )
values (#{id,jdbcType=INTEGER}, #{cat_name,jdbcType=VARCHAR}, #{cat_desc,jdbcType=VARCHAR}, 
  #{keywords,jdbcType=VARCHAR}, #{parent_id,jdbcType=INTEGER}, #{sort_order,jdbcType=INTEGER}
  )

mapper.select:
select id, cat_name, cat_desc, keywords, parent_id, sort_order
from goods_type
where id = #{id,jdbcType=INTEGER}
